INSERT INTO raw_source_cc_judgment (id, creation_date, ver, case_number, content_source_url, data_md5, processed, processing_date, publication_date, source_id, source_url, text_content, text_metadata, processing_skip_reason) VALUES (15979, '2014-07-14 09:16:30.889', 10, 'V RC 99/14', 'http://orzeczenia.ms.gov.pl/ncourt-api/judgement/content?id=154510250002512_V_RC_000099_2014_Uz_2014-04-01_001', '4f169717834788f89b44d0c57acf71d6', false, '2014-08-12 10:57:03.698', '2014-05-12 16:15:05', '154510250002512_V_RC_000099_2014_Uz_2014-04-01_001', 'http://orzeczenia.ms.gov.pl/ncourt-api/judgement/details?id=154510250002512_V_RC_000099_2014_Uz_2014-04-01_001', '<p>Sygn. akt. V RC 99/14</p>
    
    <p>
      <strong>
<!-- -->W IMIENIU RZECZYPOSPOLITEJ POLSKIEJ</strong>
</p>
    
    <p>Dnia 1 kwietnia 2014 roku</p>
    
    <p>Sąd Rejonowy dla Warszawy Pragi-Północ w <span class="anon-block">W.</span> V Wydział Rodzinny i Nieletnich</p>
    
    <p>w składzie następującym:</p>
    <p>Przewodniczący SSR Magdalena Władzińska</p>
    <p>Protokolant: Anna Tomczuk</p>
    
    <p>po rozpoznaniu w dniu 1 kwietnia 2014 roku w Warszawie</p>
    <p>na rozprawie</p>
    <p>sprawy z powództwa mał. <span class="anon-block">O. W.</span>
</p>
    <p>repr. przez <span class="anon-block">B. W.</span>
</p>
    <p>przeciwko <span class="anon-block">W. W.</span>
</p>
    <p>o zasądzenie alimentów</p>
    
    
      
      <p>I. 
        zasądza od pozwanego <span class="anon-block">W. W.</span> na rzecz córki <span class="anon-block">O. W.</span> <span class="anon-block">urodzonej (...)</span> alimenty w kwocie po 300 (trzysta) złotych miesięcznie płatne do rąk przedstawicielki ustawowej <span class="anon-block">B. W.</span> do 10-go dnia każdego miesiąca z góry wraz z ustawowymi odsetkami w przypadku uchybienia terminowi płatności którejkolwiek z rat poczynając od dnia 30 stycznia 2014 roku;</p>
    
    
      
      <p>II. 
        w pozostałej części powództwo oddala;</p>
    
    
      
      <p>III. 
        wyrokowi w punkcie I nadaje rygor natychmiastowej wykonalności;</p>
    
    
      
      <p>IV. 
        nie obciąża pozwanego <span class="anon-block">W. W.</span> kosztami nieuiszczonej opłaty sądowej przejmując ją na rachunek Skarbu Państwa.</p>', '<?xml version="1.0" encoding="UTF-8"?>
